import React from "react";
import "../stylesheets/CharacterDetail.scss";

class CharacterDetail extends React.Component {
  constructor(props) {
    super(props);
    const icon = () => {
      if (this.props.statusdetail === "Deads") {
        <i class="fas fa-skull-crossbones"></i>;
      }
    };
  }
  render() {
    return (
      <article className={`cardDetail ${this.props.id}`}>
        <div className="description">
          <h2>
            {this.props.titledetail}
            {this.props.namedetail}
          </h2>
          <p>
            <span>Status:</span> {this.props.statusdetail}
            <i class="icon">{this.props.icon}</i>
          </p>

          <p>
            <span>Species:</span> {this.props.speciesdetail}
          </p>
          <p>
            <span>Origin:</span> {this.props.origindetail.name}
          </p>
          <p>
            <span>Episodes: </span>
            {this.props.episodesdetail}
          </p>
        </div>
        <img
          className="imageDetail"
          src={this.props.imagedetail}
          alt={this.props.imagedetail}
        ></img>
      </article>
    );
  }
}

CharacterDetail.defaultProps = {};
export default CharacterDetail;
