import React from "react";
import "../stylesheets/CharacterDetail.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSkull, faPastafarianism } from "@fortawesome/free-solid-svg-icons";

class CharacterDetail extends React.Component {
  constructor(props) {
    super(props);
    // this.state = {
    //   dead: "Dead",
    //   alive: "Alive",
    // };
  }

  render() {
    return (
      <article className={`cardDetail ${this.props.id}`}>
        <div className="description">
          <h2>
            {this.props.titledetail}
            {this.props.namedetail}
          </h2>
          <p>
            <span>Status:</span>
            {this.props.statusdetail}
            <FontAwesomeIcon
              icon={this.props.statusdetail === "Dead" ? faSkull : null}
              className="icon"
            />
          </p>
          <p>
            <span>Species:</span> {this.props.speciesdetail}
            <FontAwesomeIcon
              icon={
                this.props.speciesdetail === "Alien" ? faPastafarianism : null
              }
              className="icon"
            />
          </p>
          <p>
            <span>Origin:</span> {this.props.origindetail.name}
          </p>
          <p>
            <span>Episodes: </span>
            {this.props.episodesdetail}
          </p>
        </div>
        <img
          className="imageDetail"
          src={this.props.imagedetail}
          alt={this.props.imagedetail}
        ></img>
      </article>
    );
  }
}

CharacterDetail.defaultProps = {};
export default CharacterDetail;
