import React, { useEffect, useState } from "react";
import CharacterList from "./CharacterList";
import api from "../services/api";
import { Route, Switch } from "react-router-dom";
import CharacterDetail from "./CharacterDetail";

/*ponemos un then para que no sea asíncrono con el fetch*/

const App = () => {
  //guardamos datos en el state
  const [characters, setCharacters] = useState([]);
  const [filterText, setFilterText] = useState("");

  useEffect(() => {
    api.getDataFromApi().then((data) => {
      setCharacters(data);
    });
  }, []);

  const handleChange = (filterText) => {
    setFilterText(filterText);
  };

  const filteredCharacters = characters.filter((eachCharacter) => {
    return eachCharacter.name.toLowerCase().includes(filterText);
  });

  //Buscamos cada personaje id para que se renderice cuando hagamos click en el link.
  const renderDetail = (props) => {
    const routeCharacterId = parseInt(props.match.params.id);

    const foundCharacter = characters.find((eachCharacter) => {
      return routeCharacterId === eachCharacter.id;
    });
    //si lo he encontrado
    if (foundCharacter !== undefined) {
      console.log(foundCharacter);
      return (
        <CharacterDetail
          namedetail={foundCharacter.name}
          statusdetail={foundCharacter.status}
          imagedetail={foundCharacter.image}
          origindetail={foundCharacter.origin}
          speciesdetail={foundCharacter.species}
        />
      );
    } else {
      return <p>Producto no encontrado</p>;
    }
  };

  return (
    <div>
      <Switch>
        <CharacterList
          className="card"
          characters={filteredCharacters}
          handleChange={handleChange}
        />
        <Route exact path="/"></Route>

        <Route path="/character-detail/:id" component={renderDetail}></Route>
      </Switch>
    </div>
  );
};
App.propTypes = {};
export default App;
